-- 1. Deletar AI decisions (via followup_executions)
DELETE FROM corev4_ai_decisions 
WHERE followup_execution_id IN (
    SELECT id FROM corev4_followup_executions WHERE contact_id = {{ $json.contact_id }}
);

-- 2. Deletar followup executions
DELETE FROM corev4_followup_executions 
WHERE contact_id = {{ $json.contact_id }};

-- 3. Deletar followup campaigns (CRÍTICO - ESTAVA FALTANDO!)
DELETE FROM corev4_followup_campaigns 
WHERE contact_id = {{ $json.contact_id }};

-- 4. Deletar histórico e dados relacionados
DELETE FROM corev4_chat_history 
WHERE contact_id = {{ $json.contact_id }};

DELETE FROM corev4_n8n_chat_histories 
WHERE session_id LIKE '%_id_{{ $json.contact_id }}';

DELETE FROM corev4_anum_history 
WHERE contact_id = {{ $json.contact_id }};

DELETE FROM corev4_followup_stage_history 
WHERE contact_id = {{ $json.contact_id }};

DELETE FROM corev4_chats 
WHERE contact_id = {{ $json.contact_id }};

DELETE FROM corev4_lead_state 
WHERE contact_id = {{ $json.contact_id }};

DELETE FROM corev4_contact_extras 
WHERE contact_id = {{ $json.contact_id }};

DELETE FROM corev4_execution_logs 
WHERE contact_id = {{ $json.contact_id }};

-- 5. Deletar o contato por último
DELETE FROM corev4_contacts 
WHERE id = {{ $json.contact_id }};

